mapper.update:
update
organizationId=#{organizationId}, partyMemberId=#{partyMemberId}, payStartDate=#{payStartDate}, payEndDate=#{payEndDate}, payDate=#{payDate}, updateTime=#{updateTime}
where id=#{id}

mapper.select:
select * from


    organizationId=#{con.organization}
    AND name LIKE #{con.name}
    AND idCardNo=#{con.idCardNo}
    AND memberId=#{con.memberId}
    AND memberType=#{con.memberType}
    AND education=#{con.education}
    AND sex=#{con.sex}
    AND stats=#{con.stats}

ORDER BY createTime DESC LIMIT #{page.page}, #{page.limit}
