mapper.update:
update
activity=#{activity}, meetingType=#{meetingType}, startTime=#{startTime}, endTime=#{endTime}, location=#{location}, theme=#{theme}, compere=#{compere}, shouldNumberOfPeople=#{shouldNumberOfPeople}, realNumberOfPeople=#{realNumberOfPeople}, content=#{content}, attendancePeople=#{attendancePeople}, absencePeople=#{absencePeople}, filePath=#{filePath}, updateTime=#{updateTime}
where id=#{id}

mapper.select:
select * from


    organizationId=#{con.organization}
    AND activityType=#{con.activityType}
    AND theme=#{con.theme}

        AND startTime=BETWEEN #{range.startTime} AND #{range.endTime}


ORDER BY createTime DESC LIMIT #{page.page}, #{page.limit}
